import React from 'react'

const maxDescriptionLength = 40;

export default function CurrentMenuItems(props) {
    return (
        <div id="current_menu_items" className="vertical_container">
            {props.menu.items.map((item, i) => (



                <div key={item.title + i} className="item_container" onClick={() => {
                    props.open(JSON.parse(JSON.stringify(item)), props.menuTitle, 1, false);
                }}>

                    <img className='item_img'
                        src={item.src ? item.src : '../../images/sandwich.png'} alt="sandwich" />

                    <span className="subHeading">
                        {item.title}
                    </span>

                    <span className="content">
                        {item.description.length > maxDescriptionLength ? { ...item }.description.substring(0, maxDescriptionLength) + '...' : item.description}
                    </span>

                    <span className="boldContent">
                        {item.price} ₪

                    </span>
                </div>

            ))}

        </div>

    )
}

