import React, { useEffect, useState } from 'react'

export default function LogIn(props) {
    const [isLogIn, setIsLogIn] = useState(true);

    const [phone, setPhone] = useState(''); // שמירת הטלפון
    const [password, setPassword] = useState(''); // שמירת הסיסמה
    // const [loadinVarify, setLoadinVarify] = useState(true); // שמירה האם הטעינה מתבצעת
    const [error, setError] = useState(null); // שמירה על הודעות שגיאה

    const handleLogIn = () => {
        // יצירת גוף הבקשה משדות הקלט
        const loginData = {
            phone: phone,
            password: password
        };
    }

    if (loadingMenu) {
        return <div className="vertical_container">
            {/* <p className='subTitle'>טוען נתונים מהשרת...</p> */}
            <img
                src='../../images/loading.gif'
                alt="loading gif"
                style={{
                    height: "60px",
                    marginTop: "30%"
                }}
            />
        </div> // הודעה בזמן שהטעינה מתבצעת
    }

    if (error) {
        return <p>חלה שגיאה בטעינת הנתונים. יש לנסות לטעון את הדף מחדש.</p>; // הצגת השגיאה אם קרתה
    }

    return (
        <div className='popUp_screen'>
            <div className='vertical_container add_to_order' style={{ minWidth: "480px" }}>
                <p className='mainTitle'> כניסה </p>
                <p className='content'> יש להתחבר על מנת לבצע שימוש במערכת </p>

                <div className='horizontal_container' style={{ width: "95%" }}>
                    <button className={(isLogIn ? 'checked_button' : 'button') + ' subHeading'} style={{ width: "40%" }}
                        onClick={() => setIsLogIn(true)}
                    >יש לי חשבון</button>
                    <button className={(!isLogIn ? 'checked_button' : 'button') + ' subHeading'} style={{ width: "40%" }}
                        onClick={() => setIsLogIn(false)}
                    >הרשמה</button>
                </div>

                {isLogIn ?
                    <div style={{ width: "80%", height: "480px" }}>
                        <p> מספר טלפון </p>
                        <input id='phone' type="tel" name="phone" placeholder='&#9742;' autoFocus maxLength={10} pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" required />
                        <p> סיסמה </p>
                        <input id='password' type='password' placeholder='&#8902; &#8902; &#8902; &#8902; &#8902;' required />

                        <button style={{ marginTop: "12px" }} type="submit" form="form1" value="Submit" className='dominantButton subHeading'
                            onClick={props.close}
                        >התחברות</button>
                        <p>
                            <a style={{ textDecoration: "none" }} className='content' href="url"> אופס... שכחתי סיסמה </a>
                        </p>

                        <div style={{ width: "100%", margin: "20px auto" }}>
                            <img src="/images/sandwich_banner.png" style={{ width: "100%", height: "240px", objectFit: "cover", objectPosition: "center", borderRadius: "15px" }} alt="logo" />
                        </div>
                    </div>
                    :
                    <div style={{ width: "80%", height: "480px" }}>
                        <p> שם פרטי </p>
                        <input id='firstName' type='text' placeholder='&#10022;' autoFocus required />
                        <p> שם משפחה </p>
                        <input id='lastName' type='text' placeholder='&#10022;' required />
                        <p> מספר טלפון </p>
                        <input id='phone' type="tel" name="phone" placeholder='&#9742;' maxLength={10} pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" required />
                        <p> אימייל </p>
                        <input id='email' type='text' placeholder='&#9993;' required />
                        <p> סיסמה </p>
                        <input id='password' type='password' placeholder='&#8902; &#8902; &#8902; &#8902; &#8902;' required />
                        <p> תאריך לידה </p>
                        <p style={{ fontSize: "12px" }}> אופציונלי - כדי שנדע מתי לפנק אתכם במתנה &#9829;</p>
                        <input id='date' type='date' pattern='' required />
                        <div className='horizontal_container' style={{ alignItems: "center" }}>
                            <input id="agree" type='checkbox' required style={{ width: "30px" }} />
                            <span>הסכמה לשמירת נתונים במערכת שלנו</span>
                        </div>
                        <button type="submit" form="form1" value="Submit" className='dominantButton subHeading'
                            onClick={props.close}
                        > סיום הרשמה והתחברות </button>
                    </div>}
            </div>
        </div>
    )
}
