import React, { useState } from 'react';

export default function Menues(props) {

    const [selectedMenuIndex, setSelectedMenuIndex] = useState(props.indexToShow);

    const handleMenuClick = (title) => {
        setSelectedMenuIndex(menuIndex);
        props.chooseMenu(menuIndex);
    };

    return (
        <div id="menues" className="vertical_container">
            {props.titles.map((title, index) => (
                <button
                    key={title}
                    className={selectedMenuIndex === index ? "menu_button_on" : "menu_button"}
                    onClick={() => handleMenuClick(index)}
                >
                    <span className="subHeading"> {title} </span>
                </button>
            ))}
        </div>
    )
}
