import React, { useState } from 'react';
import { menu as fullMenu } from './restaurantMenu.ts';

export default function Menues(props) {

    //  Track the currently selected menu index
    const [selectedMenuIndex, setSelectedMenuIndex] = useState(props.index);

    // Handle menu selection
    const handleMenuClick = (index) => {
        setSelectedMenuIndex(index); // Update the selected menu
        props.chooseMenu(index); // Call the parent's function to choose the menu
    };

    return (
        <div id="menues" className="vertical_container">

            <button
                key={"תפריט מלא"}
                className={selectedMenuIndex === -1 ? "menu_button_on" : "menu_button"}
                onClick={() => (handleMenuClick(-1))}
            >
                <span className="subHeading"> תפריט מלא </span>
            </button>

            {fullMenu.map((menu, index) => (
                <button
                    key={menu.title}
                    className={selectedMenuIndex === index ? "menu_button_on" : "menu_button"}
                    onClick={() => handleMenuClick(index)}
                >
                    <span className="subHeading"> {menu.title} </span>
                </button>
            ))}
        </div>
    )
}
