import React, { useState } from 'react'
import FullMenu from './FullMenu';
import OrderSummary from './Summary';
import AddToOrder from './AddToOrder.jsx';
import ConfirmSendOrder from './ConfirmSendOrder.jsx';
import LogIn from './LogIn.jsx';
import { useItemContext } from './EditedItemContext.jsx';

export default function OrderOnline() {

    const { setItemState, resetEditItemState } = useItemContext();

    const [openAddToCart, setOpenAddToCart] = useState(false);
    const [openConfirmSendOrder, setOpenConfirmSendOrder] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const openAdd = (item, menuTitle, options, quantity, isEditMode, indexInCart, newPrice) => {
        setItemState({
            item: item,
            menuTitle: menuTitle,
            options: options,
            quantity: quantity,
            isEditMode: isEditMode,
            indexInCart: isEditMode === true ? indexInCart : -1,
            newPrice: !isNaN(newPrice) && newPrice > -1 ? newPrice : item.price
        })
        setOpenAddToCart(true);
    }

    const closeAdd = () => {
        resetEditItemState()
        setOpenAddToCart(false);
    }

    const openSendOrder = () => {
        setOpenConfirmSendOrder(true)
    }

    const closeSendOrder = () => {
        setOpenConfirmSendOrder(false)
    }

    return (
        <>
            {!isLoggedIn ? (
                <LogIn close={() => { setIsLoggedIn(true); }} />
            ) : (
                <>
                    <div style={{
                        width: "100%", height: "92px", backgroundColor: "rgb(48, 15, 0)", borderRadius: "0px", display: "flex",borderEndEndRadius:"50px" ,justifyContent: "center", alignItems: "center"
                    }}> <img src="/images/iconLogo.png" style={{ width: "72px"}} alt="logo" />
                    </div>
                    <div id='order_online' className="horizontal_container section">
                        <FullMenu open={openAdd} close={closeAdd} />
                        <OrderSummary open={openAdd} close={closeAdd} openSendOrder={openSendOrder} />
                    </div>

                    {!isLoggedIn && <LogIn close={() => { setIsLoggedIn(true); }} />}

                    {openAddToCart && <AddToOrder close={closeAdd} />}

                    {openConfirmSendOrder && <ConfirmSendOrder back={closeSendOrder} />}

                    {/* <div className='vertical_container'>
                        <p className='mainTitle'>כותרת ראשית mainTitle</p>
                        <p className='subTitle'>כותרת משנה subTitle</p>
                        <p className='emphContent'>תיאור כותרת משנה emphContent</p>
                        <p className='subHeading'>תת כותרת subHeading</p>
                        <p className='content'>תיאור תת כותרת content</p>
                        <p className='boldContent'>תיאור תת כותרת בולטboldContent</p>
                    </div> */}
                </>
            )}
        </>
    );
}
