import React, { useState, useRef } from 'react'
import CurrentMenu from './CurrentMenu';
import Menues from './Menues';
import { menu as fullMenu } from './restaurantMenu.ts';

export default function FullMenu(props) {

    const [menuToShow, setMenuToShow] = useState(0);

    // Create a ref to store the references to the menu sections
    const menuRefs = useRef([]);

    const handleSetMenu = (chosenMenu) => {
        setMenuToShow(chosenMenu);

        // Scroll to the selected menu
        if (menuRefs.current[chosenMenu]) {
            menuRefs.current[chosenMenu].scrollIntoView({ behavior: 'smooth', block: 'start' , inline: 'center'});
        }
    }

    return (
        <div id="full_menu_div" className="horizontal_container">

            <Menues titles={fullMenu.map(menu => menu.title)} indexToShow={menuToShow} chooseMenu={handleSetMenu}></Menues>

            <div id="full_menu" className="vertical_container scrollbar">
                {
                    fullMenu.map((menu, index) => {
                        return (
                            <div
                                key={'divMenu'+index}
                                // Store the ref for each menu
                                ref={el => menuRefs.current[index] = el}
                            >
                                <CurrentMenu
                                    key={menu.title}
                                    menuIndex={index}
                                    menu={menu}
                                    open={props.open}
                                    close={props.close} />
                            </div>
                        )
                    })
                }
            </div>

        </div>
    )
}
